import React from 'react';
import Navigationbar from './Navigationbar.js'
import Projects from './projects.js'
import Translateme from './translateme.js'
import Rickandmorty from './rickandmorty.js'
import Tonester from './tonester.js'
import ScrollableAnchor from 'react-scrollable-anchor'


import './App.css';

class App extends React.Component {
    constructor(){
      super()
      this.state = {
        pageState: "home",
        sections: 0
      }
    }


  handleTranslateme=()=>{
    this.setState({
      pageState: "translateme"
    })
  }
  handleSectionOne=()=>{
    this.setState({
      section: 1
    })
    console.log
  }
  handleRickAndMorty=()=>{
    this.setState({
      pageState: "rickandmorty"
    })
  }

  handleTonester=()=>{
    this.setState({
      pageState: "tonester"
    })
  }

  handlePage=()=>{
    if(this.state.pageState === "translateme"){
      return <Translateme/> 
    }else if (this.state.pageState === "rickandmorty"){
      return <Rickandmorty/>
    }else if (this.state.pageState === "tonester"){
      return <Tonester/> 
    } else {
      return <Projects handleTranslateme={this.handleTranslateme} handleRickAndMorty={this.handleRickAndMorty} handleTonester={this.handleTonester}/>
    }
  }
  render(){
  return (
    <div className="App">
     <Navigationbar handleSectionOne={this.handleSectionOne}/>
    {this.handlePage()}
    </div>
  );
  }
}

export default App;
