import React from 'react';
import { Header, Embed, Divider, Button } from 'semantic-ui-react'

export default class About extends React.Component{
    state = {}

    handleClick = () => this.setState({ active: true })


    render(){
        const { active } = this.state
        return(
            <div>
                <Header color='blue' as='h1'>Mario Rodriguez</Header>
                <Header color='blue' as='h1'>Software Engineer | React.js, Ruby on Rails, Javascript</Header>
                <Embed
                    active={active}
                    icon='arrow circle down'
                    id='iitzjoker'
                    placeholder='/images/image-16by9.png'
                    source='https://www.twitch.tv/iitzjoker'
                    />

                    <Divider hidden />

                    <Button
                    content='Activate'
                    icon='bomb'
                    labelPosition='left'
                    onClick={this.handleClick}
                    />
            </div>
        )
    }
}
