import Tonester from './tonester.js'


import './App.css';


const style = {
  borderRadius: 40,
  opacity: 1.7,
  padding: '1em',
}


class App extends React.Component {
    constructor(){
      super()
      this.state = {
        pageState: "home"
      }
    }
  handleTranslateme=()=>{
    this.setState({
      pageState: "translateme"
    })
  }

  handleRickAndMorty=()=>{
    this.setState({
      pageState: "rickandmorty"
    })
  }

  handleTonester=()=>{
    this.setState({
      pageState: "tonester"
    })
  }

  handlePage=()=>{
    if(this.state.pageState === "translateme"){
      return <Translateme/> 
    }else if (this.state.pageState === "rickandmorty"){
      return <Rickandmorty/>
    }else if (this.state.pageState === "tonester"){
      return <Tonester/> 
    } else {
      return <Projects handleTranslateme={this.handleTranslateme} handleRickAndMorty={this.handleRickAndMorty} handleTonester={this.handleTonester}/>
    }
  }


  render(){

    const { open, dimmer } = this.state
